            squares: [],
          };
        }

        diagonalGroups[diagonalKey].squares.push({ whiteSquare });
        scene.add(whiteSquare);
        squares.push({ whiteSquare });
      }
    }

    // Smoothly interpolate between the current and target scale
    const lerp = (start, end, t) => start * (1 - t) + end * t;

    // Function to calculate cursor speed
    const calculateCursorSpeed = (mouseX, mouseY) => {
      const timeNow = performance.now();
      const timeDelta = timeNow - lastTime;
      const dx = mouseX - lastMousePosition.x;
      const dy = mouseY - lastMousePosition.y;
      const distance = Math.sqrt(dx * dx + dy * dy);
      const speed = distance / timeDelta;

      setLastMousePosition({ x: mouseX, y: mouseY });
      setLastTime(timeNow);

      return speed;
    };

    // Detect mouse movement and update target scale for the grid based on speed
    const onMouseMove = (event) => {
      const mouseX = event.clientX - window.innerWidth / 2;
      const mouseY = -(event.clientY - window.innerHeight / 2);

      // Calculate the speed of the cursor
      const cursorSpeed = calculateCursorSpeed(event.clientX, event.clientY);

      // Calculate which column and row the mouse is over
      const col = Math.floor((mouseX + cols * squareSize / 2) / squareSize);
      const row = Math.floor((mouseY + rows * squareSize / 2) / squareSize);

      // Adjust grid size based on cursor speed (1x1 at slow speeds to 5x5 at fast speeds)
      const minSpeed = 0;
      const maxSpeed = 1.5; // Adjust this value for sensitivity
      const gridSize = Math.min(5, Math.max(1, Math.floor((cursorSpeed / maxSpeed) * 5)));

      if (row >= 0 && row < rows && col >= 0 && col < cols) {
        // Update target scale for the dynamically sized grid of squares
        for (let i = row - Math.floor(gridSize / 2); i <= row + Math.floor(gridSize / 2); i++) {
          for (let j = col - Math.floor(gridSize / 2); j <= col + Math.floor(gridSize / 2); j++) {
            if (i >= 0 && i < rows && j >= 0 && j < cols) {
              const squareIndex = i * cols + j;
              const { whiteSquare } = squares[squareIndex];

              // Set the target scale to 0.9 (for the hover effect)
              whiteSquare.userData.targetScale = 0.9;
              whiteSquare.userData.isHovered = true;

              // Clear any existing trail timeout so squares don't shrink too soon
              if (whiteSquare.userData.trailTimeout) {
                clearTimeout(whiteSquare.userData.trailTimeout);
              }

              // Start shrinking back after a short delay to create a trail effect
              whiteSquare.userData.trailTimeout = setTimeout(() => {
                whiteSquare.userData.targetScale = 0.6;
                whiteSquare.userData.isHovered = false;
              }, 250); // 250ms delay before shrinking starts
            }
          }
        }
      }
    };

