      for (const key in diagonalGroups) {
        const { squares } = diagonalGroups[key];
        const wavePhase = key * 0.15; // Slightly increase the phase offset for smoother transitions

        squares.forEach(({ whiteSquare }) => {
          // Calculate the wave undulation for non-hovered squares
          if (!whiteSquare.userData.isHovered) {
