import React, { useEffect } from 'react';
import * as THREE from 'three';

const FullScreenWaveUndulation = () => {
  useEffect(() => {
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    const camera = new THREE.OrthographicCamera(
      window.innerWidth / -2,
      window.innerWidth / 2,
      window.innerHeight / 2,
      window.innerHeight / -2,
      1,
      1000
    );
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    const squareSize = 50;
    const geometry = new THREE.PlaneGeometry(squareSize, squareSize);
    const whiteMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff, side: THREE.DoubleSide });
    const blackMaterial = new THREE.MeshBasicMaterial({ color: 0x000000, side: THREE.DoubleSide });

    const cols = Math.ceil(window.innerWidth / squareSize);
    const rows = Math.ceil(window.innerHeight / squareSize);

    const squares = [];
    const diagonalGroups = {};

    // Create and place squares in a grid (both white and black)
    for (let i = 0; i < rows; i++) {
      for (let j = 0; j < cols; j++) {
        // White square
        const whiteSquare = new THREE.Mesh(geometry, whiteMaterial);
        whiteSquare.position.set(
          (j - cols / 2) * squareSize + squareSize / 2,
          (i - rows / 2) * squareSize + squareSize / 2,
          0.1 // Layer slightly above the black square
        );

        // Black square (behind the white square)
        const blackSquare = new THREE.Mesh(geometry, blackMaterial);
        blackSquare.position.set(
          (j - cols / 2) * squareSize + squareSize / 2,
          (i - rows / 2) * squareSize + squareSize / 2,
          0 // Positioned behind the white square
        );

        const diagonalKey = i - j; // Group by diagonal index (i - j creates unique indices for diagonal rows)

        if (!diagonalGroups[diagonalKey]) {
          diagonalGroups[diagonalKey] = {
            squares: [],
          };
        }

        diagonalGroups[diagonalKey].squares.push({ whiteSquare, blackSquare });
        scene.add(blackSquare);
        scene.add(whiteSquare);
        squares.push({ whiteSquare, blackSquare });
      }
    }

    // Function to invert color for a square (works for both white and black squares)
    const invertSquareColor = (square) => {
      const isWhiteSquare = square.material === whiteMaterial;
      square.material = isWhiteSquare ? blackMaterial : whiteMaterial;

      setTimeout(() => {
        square.material = isWhiteSquare ? whiteMaterial : blackMaterial; // Revert back
      }, 300); // Revert after 300ms
    };

    // Detect mouse movement and invert color for squares around the mouse
    const onMouseMove = (event) => {
      const mouseX = event.clientX - window.innerWidth / 2;
      const mouseY = -(event.clientY - window.innerHeight / 2);

      // Calculate which column and row the mouse is over
      const col = Math.floor((mouseX + cols * squareSize / 2) / squareSize);
      const row = Math.floor((mouseY + rows * squareSize / 2) / squareSize);

      // Invert color for the 2x2 block of squares (both white and black)
      for (let i = row - 1; i <= row; i++) {
        for (let j = col - 1; j <= col; j++) {
          if (i >= 0 && i < rows && j >= 0 && j < cols) {
            const squareIndex = i * cols + j;
            const { whiteSquare, blackSquare } = squares[squareIndex];
            invertSquareColor(whiteSquare); // Invert white square
            invertSquareColor(blackSquare); // Invert black square
          }
        }
      }
    };

    document.addEventListener('mousemove', onMouseMove);

    function animate() {
      requestAnimationFrame(animate);

      const time = performance.now() * 0.0004; // Slower time to reduce wave speed

      // Create a coordinated wave that propagates diagonally across the grid
      for (const key in diagonalGroups) {
        const { squares } = diagonalGroups[key];
        const wavePhase = key * 0.15; // Slightly increase the phase offset for smoother transitions

        // Scaling logic for white and black squares
        squares.forEach(({ whiteSquare, blackSquare }) => {
          const whiteScaleFactor = Math.abs(Math.sin(time + wavePhase)) * 0.9 + 0.01; // Shrinking white squares
          const blackScaleFactor = 1 - whiteScaleFactor; // Expanding black squares behind shrinking white squares

          // Apply scaling
          whiteSquare.scale.set(whiteScaleFactor, whiteScaleFactor, 1);
          blackSquare.scale.set(blackScaleFactor, blackScaleFactor, 1);
        });
      }

      renderer.render(scene, camera);
    }

    animate();

    window.addEventListener('resize', () => {
      renderer.setSize(window.innerWidth, window.innerHeight);
      camera.left = window.innerWidth / -2;
      camera.right = window.innerWidth / 2;
      camera.top = window.innerHeight / 2;
      camera.bottom = window.innerHeight / -2;
      camera.updateProjectionMatrix();
    });

    return () => {
      document.removeEventListener('mousemove', onMouseMove);
      document.body.removeChild(renderer.domElement);
    };
  }, []);

  return null;
};

export default FullScreenWaveUndulation;
