import React, { useEffect } from 'react';
import * as THREE from 'three';

const UndulatingEffect = () => {
  useEffect(() => {
    // Set up scene, camera, and renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Create the plane geometry and material
    const geometry = new THREE.PlaneGeometry(5, 5, 64, 64); // Larger grid with more subdivisions
    const material = new THREE.ShaderMaterial({
      uniforms: {
        u_time: { value: 0.0 },
        u_mouse: { value: { x: 0.0, y: 0.0 } },
        u_resolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) }
      },
      vertexShader: `
        uniform float u_time;
        uniform vec2 u_mouse;
        varying vec2 vUv;

        void main() {
          vUv = uv;
          vec3 pos = position;
          pos.z = sin(pos.x * 5.0 + u_time) * 0.2 + sin(pos.y * 5.0 + u_time) * 0.2;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(pos, 1.0);
        }
      `,
      fragmentShader: `
        uniform float u_time;
        uniform vec2 u_mouse;
        varying vec2 vUv;

        void main() {
          float color = 0.5 + 0.5 * sin(vUv.x * 10.0 + u_time) + sin(vUv.y * 10.0 + u_time);
          gl_FragColor = vec4(vec3(color), 1.0);
        }
      `,
      side: THREE.DoubleSide,
      wireframe: true, // Optionally show the wireframe
    });

    const plane = new THREE.Mesh(geometry, material);
    scene.add(plane);
    camera.position.z = 2;

    // Handle mouse movement for interactivity
    const handleMouseMove = (event) => {
      const mouseX = (event.clientX / window.innerWidth) * 2 - 1;
      const mouseY = -(event.clientY / window.innerHeight) * 2 + 1;
      material.uniforms.u_mouse.value.x = mouseX;
      material.uniforms.u_mouse.value.y = mouseY;
    };
    document.addEventListener('mousemove', handleMouseMove);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      material.uniforms.u_time.value += 0.05;
      renderer.render(scene, camera);
    };
    animate();

    // Handle window resizing
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
      material.uniforms.u_resolution.value.set(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Cleanup when component unmounts
    return () => {
      document.body.removeChild(renderer.domElement);
      document.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return <div />;
};

export default UndulatingEffect;
