import React, { useEffect } from 'react';
import * as THREE from 'three';

const DiamondGrid = () => {
  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Create a diamond geometry
    const geometry = new THREE.PlaneGeometry(1, 1);
    const material = new THREE.MeshBasicMaterial({ color: 0x000000 });

    // Create a grid of diamonds
    const rows = 35;
    const cols = 45;
    for (let i = 0; i < rows; i++) {
      for (let j = 0; j < cols; j++) {
        const diamond = new THREE.Mesh(geometry, material);
        diamond.position.x = i * 1.5; // Adjust space between diamonds
        diamond.position.y = j * 1.5;
        diamond.rotation.z = Math.PI / 4; // Rotate to create diamond shape
        scene.add(diamond);

        // Add hover effect
        const originalColor = 0x000000;
        diamond.userData.isHovered = false;

        const raycaster = new THREE.Raycaster();
        const mouse = new THREE.Vector2();

        function onMouseMove(event) {
          mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
          mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
        }

        document.addEventListener('mousemove', onMouseMove);

        function animate() {
          requestAnimationFrame(animate);

          // Detect mouse hover over diamonds
          raycaster.setFromCamera(mouse, camera);
          const intersects = raycaster.intersectObjects(scene.children);

          for (let i = 0; i < intersects.length; i++) {
            const obj = intersects[i].object;
            if (!obj.userData.isHovered) {
              obj.userData.isHovered = true;
              obj.material.color.set(0xffffff); // Turn white on hover

              setTimeout(() => {
                obj.material.color.set(originalColor); // Back to black after 0.5s
                obj.userData.isHovered = false;
              }, 500); // 0.5s delay
            }
          }

          renderer.render(scene, camera);
        }

        animate();
      }
    }

    camera.position.z = 50;

    return () => {
      document.body.removeChild(renderer.domElement);
    };
  }, []);

  return null;
};

export default DiamondGrid;
