import React, { useEffect } from 'react';
import * as THREE from 'three';

const FullScreenSquaresGrowingShrinking = () => {
  useEffect(() => {
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    const camera = new THREE.OrthographicCamera(
      window.innerWidth / -2,
      window.innerWidth / 2,
      window.innerHeight / 2,
      window.innerHeight / -2,
      1,
      1000
    );
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    const squareSize = 50;
    const geometry = new THREE.PlaneGeometry(squareSize, squareSize);
    const material = new THREE.MeshBasicMaterial({ color: 0xffffff, side: THREE.DoubleSide });

    const cols = Math.ceil(window.innerWidth / squareSize);
    const rows = Math.ceil(window.innerHeight / squareSize);

    const squares = [];

    // Create and place squares in the grid
    for (let i = 0; i < rows; i++) {
      for (let j = 0; j < cols; j++) {
        const square = new THREE.Mesh(geometry, material);
        square.position.set(
          (j - cols / 2) * squareSize + squareSize / 2,
          (i - rows / 2) * squareSize + squareSize / 2,
          0
        );
        square.userData.offset = Math.random() * Math.PI * 2; // Random offset for individual timing
        square.userData.scaleSpeed = 0.5 + Math.random(); // Random speed of scaling
        scene.add(square);
        squares.push(square);
      }
    }

    function animate() {
      requestAnimationFrame(animate);

      const time = performance.now() * 0.001; // Time in seconds

      // Grow and shrink each square randomly over time
      squares.forEach((square) => {
        const scaleFactor = Math.sin(time * square.userData.scaleSpeed + square.userData.offset) * 0.5 + 1; // Scale between 0.5 and 1.5
        square.scale.set(scaleFactor, scaleFactor, 1); // Apply the scale factor uniformly
      });

      renderer.render(scene, camera);
    }

    animate();

    window.addEventListener('resize', () => {
      renderer.setSize(window.innerWidth, window.innerHeight);
      camera.left = window.innerWidth / -2;
      camera.right = window.innerWidth / 2;
      camera.top = window.innerHeight / 2;
      camera.bottom = window.innerHeight / -2;
      camera.updateProjectionMatrix();
    });

    return () => {
      document.body.removeChild(renderer.domElement);
    };
  }, []);

  return null;
};

export default FullScreenSquaresGrowingShrinking;
